import { defineConfig } from 'astro/config';
import react from "@astrojs/react";

import tailwind from "@astrojs/tailwind";

// https://astro.build/config
export default defineConfig({
  integrations: [react(), tailwind()],
});

import { Card, Title, BarChart, LineChart } from "@tremor/react";

const CompanyCharts = ({ data2022, data2023 }) => {
  const publicDebtData = [
    {
      year: "2022",
      "Debt to DOY": data2022?.debtToDOY,
      "Debt to Customs": data2022?.debtToCustoms,
      "Co-collected Public": data2022?.coCollectedPublic,
    },
    {
      year: "2023",
      "Debt to DOY": data2023?.debtToDOY,
      "Debt to Customs": data2023?.debtToCustoms,
      "Co-collected Public": data2023?.coCollectedPublic,
    },
  ];

  const efkaDebtData = [
    {
      year: "2022",
      "Debt to EFKA": data2022?.debtToEFKA,
      "Co-collected EFKA": data2022?.coCollectedEFKA,
    },
    {
      year: "2023",
      "Debt to EFKA": data2023?.debtToEFKA,
      "Co-collected EFKA": data2023?.coCollectedEFKA,
    },
  ];

  const totalDebtData = [
    {
      year: "2022",
      "Public Debt": data2022?.totalPublic,
      "EFKA Debt": data2022?.totalEFKA,
      "Total Debt": data2022?.totalPublic + data2022?.totalEFKA,
    },
    {
      year: "2023",
      "Public Debt": data2023?.totalPublic,
      "EFKA Debt": data2023?.totalEFKA,
      "Total Debt": data2023?.totalPublic + data2023?.totalEFKA,
    },
  ];

  return (
    <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
      <Card>
        <Title>Public Debt Breakdown</Title>
        <BarChart
          className="mt-6"
          data={publicDebtData}
          index="year"
          categories={["Debt to DOY", "Debt to Customs", "Co-collected Public"]}
          colors={["blue", "teal", "amber"]}
          valueFormatter={(number) =>
            `‚Ç¨ ${Intl.NumberFormat("us").format(number).toString()}`
          }
          yAxisWidth={48}
        />
      </Card>

      <Card>
        <Title>EFKA Debt Breakdown</Title>
        <BarChart
          className="mt-6"
          data={efkaDebtData}
          index="year"
          categories={["Debt to EFKA", "Co-collected EFKA"]}
          colors={["indigo", "violet"]}
          valueFormatter={(number) =>
            `‚Ç¨ ${Intl.NumberFormat("us").format(number).toString()}`
          }
          yAxisWidth={48}
        />
      </Card>

      <Card className="col-span-1 sm:col-span-2">
        <Title>Total Debt Trend</Title>
        <LineChart
          className="mt-6"
          data={totalDebtData}
          index="year"
          categories={["Public Debt", "EFKA Debt", "Total Debt"]}
          colors={["emerald", "rose", "blue"]}
          valueFormatter={(number) =>
            `‚Ç¨ ${Intl.NumberFormat("us").format(number).toString()}`
          }
          yAxisWidth={48}
        />
      </Card>
    </div>
  );
};

export default CompanyCharts;

import React, { useState, useEffect, useCallback } from "react";
import {
  Card,
  Title,
  Table,
  TableHead,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell,
  TextInput,
  Select,
  SelectItem,
  Badge,
  Bold,
} from "@tremor/react";
import { ArrowUpDown, ChevronLeft, ChevronRight, ShieldCheck } from "lucide-react";
import { OramaClient } from "@oramacloud/client";

const EnhancedCompanyTable = ({ year }) => {
  const [sortColumn, setSortColumn] = useState("totalPublic");
  const [sortDirection, setSortDirection] = useState("desc");
  const [searchTerm, setSearchTerm] = useState("");
  const [deferredSearchTerm, setDeferredSearchTerm] = useState("");
  const [debtFilter, setDebtFilter] = useState("all");
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [cache, setCache] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [totalItems, setTotalItems] = useState(0);
  const itemsPerPage = 50;

  const client = new OramaClient({
    endpoint: 'https://cloud.orama.run/v1/indexes/nomika2023-qtw735',
    api_key: 'iHhGT92IYaCRjJ7ibbmK8xJYOJ0AtHPY'
  });

  const PaginationControls = () => (
    <div className="flex items-center justify-between mt-4 mb-4">
      <Badge icon={ShieldCheck} color='emerald'><Bold>{totalItems}</Bold> œÉœÖŒΩŒøŒªŒπŒ∫Œ¨ Œ±œÄŒøœÑŒµŒªŒ≠œÉŒºŒ±œÑŒ±</Badge>
      <div className="flex items-center space-x-2">
        <button
          type="button"
          onClick={handlePreviousPage}
          disabled={currentPage === 1}
          className="p-2 border rounded-full disabled:opacity-50"
        >
          <ChevronLeft size={20} />
        </button>
        <span>
          Œ£ŒµŒªŒØŒ¥Œ± {currentPage} Œ±œÄœå {totalPages}
        </span>
        <button
          type="button"
          onClick={handleNextPage}
          disabled={currentPage === totalPages}
          className="p-2 border rounded-full disabled:opacity-50"
        >
          <ChevronRight size={20} />
        </button>
      </div>
    </div>
  );

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    setSearchTerm(params.get("searchTerm") || "");
    setSortColumn(params.get("sortColumn") || "totalPublic");
    setSortDirection(params.get("sortDirection") || "desc");
    setDebtFilter(params.get("debtFilter") || "all");
    setCurrentPage(parseInt(params.get("page"), 10) || 1);
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (searchTerm.length >= 2) {
        setDeferredSearchTerm(searchTerm);
      } else {
        setDeferredSearchTerm("");
      }
    }, 300);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  const updateURLParams = () => {
    const params = new URLSearchParams();
    if (searchTerm) params.set("searchTerm", searchTerm);
    if (sortColumn) params.set("sortColumn", sortColumn);
    if (sortDirection) params.set("sortDirection", sortDirection);
    if (debtFilter) params.set("debtFilter", debtFilter);
    if (currentPage) params.set("page", currentPage);
    window.history.pushState({}, '', `${window.location.pathname}?${params.toString()}`);
  };

  useEffect(() => {
    updateURLParams();
  }, [searchTerm, sortColumn, sortDirection, debtFilter, currentPage]);

  // biome-ignore lint/correctness/useExhaustiveDependencies: <explanation>
  const fetchData = useCallback(async () => {
    const cacheKey = `${deferredSearchTerm}-${debtFilter}-${sortColumn}-${sortDirection}-${currentPage}`;
    if (cache[cacheKey]) {
      setCompanies(cache[cacheKey].companies);
      setTotalItems(cache[cacheKey].count);
      return;
    }

    setLoading(true);
    try {
      const searchParams = {
        term: deferredSearchTerm,
        limit: itemsPerPage,
        offset: (currentPage - 1) * itemsPerPage,
        sortBy: {
          property: sortColumn,
          order: sortDirection.toUpperCase()
        },
        mode: 'fulltext'
      };

      if (debtFilter !== 'all') {
        searchParams.where = {
          totalPublic: debtFilter === 'high' ? { gt: 1000000000 } :
                       debtFilter === 'medium' ? { gt: 500000000, lte: 1000000000 } :
                       { lte: 500000000 }
        };
      }

      const results = await client.search(searchParams);
      const newCompanies = results.hits.map(hit => hit.document);
      setCompanies(newCompanies);
      setTotalItems(results.count);
      setCache(prevCache => ({ ...prevCache, [cacheKey]: { companies: newCompanies, count: results.count } }));
    } catch (err) {
      console.error("Error fetching data:", err);
      setError(err.message || "An error occurred while fetching data");
    } finally {
      setLoading(false);
    }
  }, [deferredSearchTerm, debtFilter, sortColumn, sortDirection, currentPage, client]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const formatCurrency = (amount) => {
    const inThousands = amount ;
    return new Intl.NumberFormat("el-GR", {
      style: "currency",
      currency: "EUR",
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(inThousands);
  };

  const handleSort = (column) => {
    if (column === sortColumn) {
      setSortDirection(sortDirection === "asc" ? "desc" : "asc");
    } else {
      setSortColumn(column);
      setSortDirection("asc");
    }
    setCurrentPage(1);
  };

  const totalPages = Math.ceil(totalItems / itemsPerPage);

  const handlePreviousPage = () => {
    setCurrentPage((prev) => Math.max(prev - 1, 1));
  };

  const handleNextPage = () => {
    setCurrentPage((prev) => Math.min(prev + 1, totalPages));
  };

  if (error) return <div>Error: {error}</div>;

  return (
    <>
      <Title>Company List {year}</Title>
      <div className="flex justify-between items-center mt-4 mb-4">
        <TextInput
          placeholder="ŒëŒΩŒ±Œ∂ŒÆœÑŒ∑œÉŒ∑ (ŒºŒµ ŒïœÄœâŒΩœÖŒºŒØŒ± ŒÆ ŒëŒ¶Œú)..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value.toUpperCase())}
        />
        <Select value={debtFilter} onValueChange={setDebtFilter}>
          <SelectItem value="all">All Debts</SelectItem>
          <SelectItem value="high">High Debt (1B)</SelectItem>
          <SelectItem value="medium">Medium Debt (500M-1B)</SelectItem>
          <SelectItem value="low">Low Debt (500M)</SelectItem>
        </Select>
      </div>
      <PaginationControls />
      <Table>
        <TableHead>
          <TableRow>
            <TableHeaderCell className="w-1/2">ŒïœÄœâŒΩœÖŒºŒØŒ±</TableHeaderCell>
            <TableHeaderCell
              className="cursor-pointer w-1/4"
              onClick={() => handleSort("totalPublic")}
            >
              Œ£œçŒΩ. ŒüœÜŒµŒπŒªœéŒΩ ŒîŒ∑ŒºŒøœÉŒØŒøœÖ<ArrowUpDown className="inline" size={16} />
            </TableHeaderCell>
            <TableHeaderCell
              className="cursor-pointer w-1/4"
              onClick={() => handleSort("totalEFKA")}
            >
              Œ£œçŒΩ. ŒüœÜŒµŒπŒªœéŒΩ ŒïŒ¶ŒöŒë <ArrowUpDown className="inline" size={16} />
            </TableHeaderCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {companies.map((company) => (
            <TableRow key={company.id}>
              <TableCell className="max-w-72">
                <a
                  href={`/company/${company.afm}`}
                  className="text-blue-500 hover:underline truncate block max-w-full"
                  title={company.name}
                >
                  {company.name}
                </a>
              </TableCell>
              <TableCell>{formatCurrency(company.totalPublic)}</TableCell>
              <TableCell>{formatCurrency(company.totalEFKA)}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      {loading && <div className="mt-4 text-center">Loading more results...</div>}
      <PaginationControls />
    </>
  );
};

export default EnhancedCompanyTable;

// File: src/pages/api/companies.js
import companies2022 from '../../data/nomika_2022.json';
import companies2023 from '../../data/nomika_2023.json';

export async function get({ request }) {
  const url = new URL(request.url);
  const year = url.searchParams.get('year') || '2023';
  const page = parseInt(url.searchParams.get('page') || '1');
  const limit = parseInt(url.searchParams.get('limit') || '50');
  const sortBy = url.searchParams.get('sortBy') || 'name';
  const sortOrder = url.searchParams.get('sortOrder') || 'asc';
  const search = url.searchParams.get('search') || '';
  const debtFilter = url.searchParams.get('debtFilter') || 'all';

  const companies = year === '2022' ? companies2022 : companies2023;

  // Filter
  let filteredCompanies = companies.filter(company =>
    company.name.toLowerCase().includes(search.toLowerCase()) &&
    (debtFilter === 'all' ||
      (debtFilter === 'high' && company.totalPublic > 1000000000) ||
      (debtFilter === 'medium' && company.totalPublic > 500000000 && company.totalPublic <= 1000000000) ||
      (debtFilter === 'low' && company.totalPublic <= 500000000))
  );

  // Sort
  filteredCompanies.sort((a, b) => {
    if (a[sortBy] < b[sortBy]) return sortOrder === 'asc' ? -1 : 1;
    if (a[sortBy] > b[sortBy]) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  // Paginate
  const startIndex = (page - 1) * limit;
  const endIndex = page * limit;
  const paginatedCompanies = filteredCompanies.slice(startIndex, endIndex);

  return new Response(JSON.stringify({
    companies: paginatedCompanies,
    totalPages: Math.ceil(filteredCompanies.length / limit),
    totalItems: filteredCompanies.length
  }), {
    status: 200,
    headers: {
      "Content-Type": "application/json"
    }
  });
}

